import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [formOpen, setFormOpen] = useState(false);

  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList />
        <FormAddFriends formOpen={formOpen} setFormOpen={setFormOpen} />
      </div>
    </div>
  );
}

function FriendsList() {
  return (
    <ol>
      <Friends />
    </ol>
  );
}

function Friends() {
  return initialFriends.map((f) => (
    <li key={f.id}>
      <img src={f.image} alt={f.name} />
      <h3>{f.name}</h3>
      {f.balance < 0 && (
        <p className="red">
          You owe {f.name} ${Math.abs(f.balance)}
        </p>
      )}

      {f.balance > 0 && (
        <p className="green">
          {f.name} owes you ${f.balance}
        </p>
      )}

      {f.balance === 0 && <p>You and {f.name} are even</p>}

      <Button>Select</Button>
      <Button value={true}>Add Friends</Button>
    </li>
  ));
}

function Button({ children, value, setFormOpen }) {
  return (
    <button onClick={() => setFormOpen(value)} className="button">
      {children}
    </button>
  );
}

function FormAddFriends({ formOpen, setFormOpen }) {
  return (
    formOpen && (
      <form className="form-add-friend">
        <label>ðŸ«‚Friend Name</label>
        <input type="text" />

        <label>ðŸ“·Image URL</label>
        <input type="text" />

        <Button value={false} setFormOpen={setFormOpen}>
          Close
        </Button>
      </form>
    )
  );
}
