import { useState, useEffect } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [friend, setFriend] = useState(initialFriends);
  const [formOpen, setFormOpen] = useState(false);

  function handelAddFriend(f) {
    setFriend((friend) => [...friend, f]);
  }

  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList friend={friend} setFormOpen={setFormOpen} />
        <FormAddFriends
          handelAddFriend={handelAddFriend}
          setFriend={setFriend}
          formOpen={formOpen}
          setFormOpen={setFormOpen}
        />
        {formOpen ? (
          <Button onClick={() => setFormOpen(false)}>Close</Button>
        ) : (
          <Button onClick={() => setFormOpen(true)}>Add Friends</Button>
        )}
      </div>
      <Form />
    </div>
  );
}

function FriendsList({ friend, setFormOpen }) {
  return (
    <ol>
      <Friends friend={friend} setFormOpen={setFormOpen} />
    </ol>
  );
}

function Friends({ friend, setFormOpen }) {
  return friend.map((f) => (
    <li key={f.id}>
      <img src={f.image} alt={f.name} />
      <h3>{f.name}</h3>
      {f.balance < 0 && (
        <p className="red">
          You owe {f.name} ${Math.abs(f.balance)}
        </p>
      )}

      {f.balance > 0 && (
        <p className="green">
          {f.name} owes you ${f.balance}
        </p>
      )}

      {f.balance === 0 && <p>You and {f.name} are even</p>}

      <Button>Select</Button>
    </li>
  ));
}

function Button({ children, onClick }) {
  return (
    <button onClick={onClick} className="button">
      {children}
    </button>
  );
}

function FormAddFriends({ handelAddFriend, formOpen, setFormOpen }) {
  const randomNumbers = Math.floor(Math.random() * 100) + 1;

  const [name, setName] = useState("");
  const [image, setImg] = useState("https://i.pravatar.cc/48");

  function handleSubmit(e) {
    e.preventDefault();

    if (!name || !image) return;

    const newFriend = {
      name,
      image: `${image}?=${randomNumbers}`,
      balance: 0,
      id: randomNumbers,
    };
    handelAddFriend(newFriend);

    setFormOpen(false);
    setName("");

    console.log(randomNumbers);
  }

  return (
    formOpen && (
      <form className="form-add-friend" onSubmit={handleSubmit}>
        <label>🫂Friend Name</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />

        <label>📷Image URL</label>
        <input
          type="text"
          value={image}
          onChange={(e) => setImg(e.target.value)}
        />

        <Button onClick={handleSubmit}>Add</Button>
      </form>
    )
  );
}

function Form() {
  return (
    <form className="form-split-bill">
      <h2>Split a bill with X</h2>

      <label>🤑Bill Value</label>
      <input type="number" />

      <label>🧍Your Expenses</label>
      <input type="number" />

      <label>🧑‍🤝‍🧑Your Friend Expenses</label>
      <input type="number" disabled />

      <label>Who's paying the bill?</label>
      <select>
        <option value="user">You</option>
        <option value="other">X</option>
      </select>

      <Button>Split Bill</Button>
    </form>
  );
}
